import React, { useState, useEffect } from "react";
import Autocomplete from 'react-autocomplete';
import axios from 'axios'
// import { useHistory } from 'react-router-dom';
// import {Link} from "react-router-dom";
// import {Link,useLocation,withRouter,Redirect} from "react-router-dom";


function AutoComo(props) {

  useEffect(() => {
    // console.log("props",props)
  },[])

  // const history = useHistory();
  // const goLogin = () => history.push('login');

  // const [value,setVal] = useState({ title: 'The Shawshank Redemption', year: 1994 })
        const [value,setVal] = useState()
        const [items,setItems] = useState([])


        // var value = ""

        function handleChange(VariableName){
          setItems([]);
          if(VariableName == ""){

            return
          }
          var url = 'https://elastic.airesearch.in/economics_v102_test/_search'
          var query = {
            "query": {
              "bool": {
                "must": [
                  {"fuzzy": {"VariablePath": VariableName}}
                ]
              }
            },
            "aggs": {
              "varpath" : {
                "terms": {
                  "field": "VariablePath.keyword"
                }
              }
            },
            "size": 0
          }

          var query2 = {
            "query": {
                "bool": {
                  "must":[
                      {
                        "query_string":{
                        "fields": ["HSCode^4","Level5","HSName^4","path","HSCodeName^4"],
                          "query": `${VariableName}~`,
                          "minimum_should_match": 1
                        }
                      }
                  ]
                }
            }
          }

          var query3 = {
            "query": {
              "bool": {
                "must": [
                  {"multi_match": {
                    "query": VariableName,
                    "fields": ["HSCode","Level5","HSName.raw"]
                  }}
                ]
              }
            }
          }

		  var query4 = {   
					"_source":["HSCode","HSName","HSCodeName","VariableName","VariablePath","Country"],
					"query": {
					"bool": {
						"must":[
							{
							"query_string":{
									"fields": ["HSCode^3","HSName^2","HSCodeName^2","VariableName^5","VariablePath"],
									"query": `${VariableName}~`,
									"minimum_should_match": 1
								}
							}
						]
					}
				}
			}





            axios.post(url, query4)
              .then( d => {
                if(d.status === 200 ){
                  console.log(d.data);
                  let total_data = d.data.hits.hits

                  // let list = data_list.map(s => {
                  //   // return {'label' :s.key.split("/").join(" > "),'path':s.key}

                  //   return {
                  //     'title':s.key,
                  //     'label' :s.key.split("/").join(" > "),
                  //     'path':"chart/"+s.key.substring(s.key.indexOf('/')+1)
                  //   }
                  //   return {
                  //     'title':s.key,
                  //     'label' :s.key.split("/").join(" > "),
                  //     'path':"chart/"+s.key.substring(s.key.indexOf('/')+1)
                  //   }
                  // })


                  let list = total_data.map(item => {
                    let title = ""
                    let path = ""
                    let label;
                    let index = item._index

                    if(index === "economics_v102_test"){

                      path = item._source.Path
                      if(!path){
                        path = " "
                      }
                      // title = `Path : ${path}       VARIABLE Name -${item._source.VariableName}        PRODUCT : AI-TradeAnalytics`
                      title = `VARIABLE Name -${item._source.VariableName}    Path : ${path}${item._source.VariableName}       PRODUCT : AI-Economics`;


                      // label = path.split("/").join(" > ");
                      // label = title;
                      path = `AI-Economics/${path}${item._source.VariableName}`;
                    }
                    else if(index == "trade_import_yearly_v1"){
                      // title = `HSCodeName - ${item._source.HSCodeName}      HSCode - ${item._source.HSCode}       PRODUCT : AI-Economics`;
                      title = `HSCode - ${item._source.HSCode}   Country - ${item._source.Country}    PRODUCT : AI-TradeAnalytics`;
                      path = `AI-TradeAnalytics/${item._source.HSCode}`

                      // label = title;
                    }

                    return {
                      'title':title,
                      'label':title,
                      'path':path,
                    }

                  })
                  console.log(list)
                  setItems(list)
                }
                else{
                  console.log("err")
                }
              })
              .catch( error => {
                if (error) {
                  console.log(error)
                }
                else{
                  console.log("Error!");
                }
              });
        }

  // const classes = useStyles();

  return (
    <div>

      <Autocomplete
            getItemValue={(item) => item.path}
            items={items}
            renderItem={(item, isHighlighted) =>
              <div style={{ background: isHighlighted ? 'lightgray' : 'white' }}>

                {/* <Link to={`/${item.path}`}>{item.label}</Link> */}

              </div>
            }
            value={value}
            onChange={(e) => {setVal(e.target.value);handleChange(e.target.value)}}
            // onSelect={(val) => {setVal(val);console.log("val",val)}}
        />
		<br /> <br />

	{/* JSON.stringify(items) */}
    </div>
  );
}




export default AutoComo ;
